n.";			if(empty($step['site_admin']))		$error[] = "Por favor escribe la dirección de la administración.";			if(empty($step['site_resources']))		$error[] = "Por favor escribe la dirección de los recursos de tu aplicación.";			if(empty($step['site_resources_sys']))		$error[] = "Por favor escribe la dirección de los recursos globales para tus aplicaciones.";			if($step['security_level'] < 0 OR $step['security_level'] > 5 OR !is_numeric($step['security_level']))		$error[] = "Selecciona un sistema de codificación válido.";			if(empty($step['security_hash']) OR strlen($step['security_hash']) < 20)		$error[] = "Por favor escribe una clave de codificación con más de 20 caracteres.";			if(!empty($step['errors_email_to']) AND !isValid($step['errros_email_to']))		$error[] = "Por favor escribe un correo electrónico de reportes válido.";			if($step['server_limit_load'] < 0 OR $step['server_limit_load'] > 100 OR !is_numeric($step['server_limit_load']))		$error[] = "Selecciona un limite de carga de CPU válido.";				if($step['logs_save'] == "error" OR $step['logs_save'] == "warning" OR $step['logs_save'] == "info" OR $step['logs_save'] == "onerror")		$step['logs_save'] = "'$step[logs_save]'";				if(empty($error))		$sql = mysql_connect($step['mysql_host'], $step['mysql_user'], $step['mysql_pass']) or $error[] = "No hemos podido establecer una conexión con el Servidor MySQL. Revise que los datos de conexión sean correctos y que el servidor se encuentre disponible.";			if(empty($error))		mysql_query("CREATE DATABASE IF NOT EXISTS $step[mysql_name];") or $error[] = "No se ha podido crear la base de datos correctamente, es probable que el usuario que hayas escrito para la conexión no tenga los permisos necesarios.";			if(empty($error))		mysql_select_db($step['mysql_name']) or $error[] = "No se ha podido encontrar la base de datos para BeatRock. Este es un error desconocido, intentalo de nuevo más tarde.";			if(empty($error))	{		if($P['nodb'] !== "true")			CreateDB($sql);					file_put_contents("../Kernel/Secret_Hash", $step['security_hash']);				if(isset($P['save']))		{			$config = file_get_contents("Configuration");						foreach($step as $param => $value)				$config = str_replace("{" . $param . "}", $value, $config);						$write = file_put_contents("../Kernel/Configuration.php", $config);			file_put_contents("../Kernel/Configuration.Backup.php", $config);						if(!$write)			{				header("Location: ./Config");				exit;			}						header("Location: ./step3");			exit;		}		else if(isset($P['show']))		{			foreach($step as $param => $value)				$_SESSION['step2'][$param] = $value;							header("Location: ./Config");			exit;		}	}}$page['name'] = "Configuración Maestra";require('Header.php');?><div class="content">	<p>		BeatRock precisa de un archivo de configuración para definir algunas de sus funcionalidades, el siguiente paso creará el archivo de configuración automaticamente acorde al siguiente formulario:	</p>		<div class="of"></div>		<form action="./step2?do=save" method="POST">		<section>			<h2>Base de datos (MySQL)</h2>						<p>				<label for="mysql_host">Host:</label>				<input type="text" name="mysql_host" id="mysql_host" placeholder="localhost" value="<?=$step['mysql_host']?>" required autofocus autocomplete="off" />								<span>Host a donde se realizará la conexión al servidor MySQL. Predeterminado: localhost</span>			</p>						<p>				<label for="mysql_user">Nombre de usuario:</label>				<input type="text" name="mysql_user" id="mysql_user" placeholder="root" value="<?=$step['mysql_user']?>" required autocomplete="off" />								<span>Nombre de usuario para la autenticación con el servidor MySQL.</span>			</p>						<p>				<label for="mysql_pass">Contraseña:</label>				<input type="password" name="mysql_pass" id="mysql_pass" value="<?=$step['mysql_pass']?>" required autocomplete="off" />								<span>Contraseña para la autenticación con el servid