<?php####################################################### 					 BeatRock				   	   ######################################################### Framework avanzado de procesamiento para PHP.   ######################################################### InfoSmart © 2012 Todos los derechos reservados. #### http://www.infosmart.mx/						   ######################################################### http://beatrock.infosmart.mx/				   #######################################################// Acción ilegal.if(!defined('BEATROCK'))	exit;	class Tpl{	public static $html = "";	public static $lang = "";	private static $params = Array();		public static $metas = "";	public static $styles = "";	public static $js = "";	public static $vars = "";	public static $stuff = "";	public static $javascript = "";	public static $mhtml = "";	public static $mhead = "";		// Función privada - Lanzar error.	// - $function: Función causante.	// - $msg: Mensaje del error.	private static function Error($code, $function, $msg = '')	{		BitRock::setStatus($msg, __FILE__, Array('function' => $function));		BitRock::launchError($code);	}		// Función - Cargar una plantilla.	// - $id: Identificación de variables a usar.	public static function Load()	{		$cache = self::GetCache();				if($cache !== false)		{			self::$html = $cache;			return true;		}				extract($GLOBALS);				$header = true;		$footer = true;		$folder = "";				if(Core::IsMobile() AND $site['site_mobile'] == 'true')		{			if(is_array($page['folder']))			{				$pa = $page['folder'];				$page['folder'] = array_merge(Array('mobile'), $pa);			}			else				$page['folder'] = Array('mobile', $page['folder']);		}				if(is_array($page['folder']))		{			$pa = $page['folder'];			$page['folder'] = "";						foreach($pa as $fo)				$page['folder'] .= "$fo" . DS;		}				$template = $page['id'];				$folder = $page['folder'];					if(isset($page['header']))			$header = $page['header'];					if(isset($page['footer']))			$footer = $page['footer'];					if(!empty($folder))		{			if(substr($folder, -1) !== DS AND substr($folder, -1) !== '/')				$folder .= DS;		}					if(is_array($template))		{			$html = "";						foreach($template as $t)				$html .= self::Process(TEMPLATES . $folder . $t);		}		else			$html = self::Process(TEMPLATES . $folder . $template);					$html = self::Compress($html);		ob_start();							if($header)		{			$h = 'Header';						if(Core::IsMobile() AND $site['site_mobile'] == 'true')				$h = 'Header.Mobile';							if(!file_exists(HEADERS . $h . '.php'))				self::Error('02t', __FUNCTION__, 'No ha sido posible cargar la cabecera "' . HEADERS . $h . ' .php".');						require(KERNEL . 'Functions.Header.php');						if(!empty($page['site_name']))				$site['name'] = $page['site_name'];			else			{				if(empty($page['name']))					$page['name'] = $site['site_slogan'];									$site['name'] = SITE_NAME;				$site['name'] .= !empty($page['name']) ? " $site[site_separation] " : "";				$site['name'] .= $page['name'];			}						if(empty($page['subheader']))				$page['subheader'] = 'SubHeader';							if(!file_exists(HEADERS . $page['subheader'] . '.php') AND $page['subheader'] !== 'none')				self::Error('02t', __FUNCTION__, "No ha sido posible cargar la subcabecera '" . HEADERS . " $page[subheader].php'.");						require(HEADERS . $h . '.php');						if($page['subheader'] !== "none")				require(HEADERS . $page['subheader'] . '.php');		}				echo $html;				if($footer)		{			if(empty($page['subfooter']))				$page['subfooter'] = 'SubFooter';							if(!file_exists(HEADERS . $page['subfooter'] . '.php') AND $page['subfooter'] !== 'none')				self::Error('02t', __FUNCTION__, "No ha sido posible cargar el sub pie de página '" . HEADERS . " $page[subfooter].php'.");							if($page['subfooter'] !== 'none')				require(HEADERS . $page['subfooter'] . '.php');						require(HEADERS . 'Footer.php');		}						$html = ob_get_contents();				$lang = self::SetLang($html, $page['lang'], $page['lang_param']);		$html = $lang[0];				$html = self::SetParams($html, $page['parid']);				self::$html = $html;		self::$lang = $lang[1];				ob_clean();	}		// Función - Procesar una plantilla (TPL) y obtener su contenido en HTML.	// - $tpl: Ubicación de la plantilla.	// - $extra (Bool): ¿Aplicar las variables y comprimir HTML?	public static function Process($tpl, $extra = false)	{				ob_start();				extract($GLOBALS);				if(!file_exists($tpl . '.tpl'))			self::Error('01t', __FUNCTION__, 'No ha sido posible cargar la plantilla especificada "'.$tpl.'".');				require($tpl . '.tpl');		$html = ob_get_contents();				if($extra)		{			$lang = self::SetLang($html, $page['lang']);			$html = $lang[0];						$html = self::SetParams($html);						$html = self::Compress($html);		}				ob_clean();		return $html;			}		// Función - Aplicar variables al código HTML.	// - $html: Código HTML.	// - $id: Identificación de variables.	public static function SetParams($html, $id = "%")	{		if(empty($id))			$id = "%";					if(!empty(self::$params))		{			foreach(self::$params as $p => $v)				$html = str_ireplace($id . $p . $id, $v, $html);		}				return $html;	}		// Función - Aplicar variables de traducción al código HTML.	// - $html: Código HTML.	// - $lang: Código de lenguaje.	// - $param: Parametro de traducción.	public static function SetLang($html, $lang = "", $param = "||")	{		global $site, $page;		if($site['site_translate'] == "false")			return Array($html, $lang);				if(empty($lang))			$lang = LANG;		if(empty($param))			$param = "||";					if(!empty($site['site_translate']))			$lang = $site['site_translate'];				$r = Site::GetTranslations($lang);					if($r == false)			return Array(preg_replace("/\\$param(.*?)\\$param/is", '${1}', $html), $lang);					while($row = mysql_fetch_assoc($r))		{						if(!empty($row['var']))				$html = str_ireplace($param . "$row[var]" . $param, $row['translated'], $html);							if(!empty($row['original']))				$html = str_ireplace($param . "$row[original]" . $param, $row['translated'], $html);						if($site['site_smart_translate'] == "true" OR $page['smart_translate'] == true)				$html = str_replace($row['original'], $row['translated'], $html);		}				$html = preg_replace("/\\$param(.*?)\\$param/is", '${1}', $html);		return Array($html, $lang);	}		// Función - Comprimir código HTML.	// - $html: Código HTML.	public static function Compress($html)	{		global $config, $page;				if(BROWSER !== "Internet Explorer" AND BROWSER !== "Internet Explorer 9" AND $page['compress'] !== false)		{			if($config['server']['compression'] OR $page['compress'] == true)				$html = Core::Compress($html);		}				return $html;	}		// Función - Guardar caché de la página actual.	public static function SaveCache()	{		global $page;				$cache = Site::GetCache($page['id']);		if(!$cache)			return false;		if(!Mem::Ready())		{			$file = BIT . 'Cache' . DS . $page['id'] . '.' . self::$lang . '.cache';						if(time() > (filemtime($file) + ($cache['time'] * 60 * 60)) AND file_exists($file))				return false;					return Io::Write($file, Tpl::$html);		}		else		{			$time = Mem::Get($page['id'] . self::$lang . '_time');			if(time() < ($time + ($cache['time'] * 60 * 60)) AND !empty($time))				return false;			Mem::Set($page['id'] . self::$lang . '_time', time());			Mem::Set($page['id'] . self::$lang, self::$html);			return true;		}	}		// Función - Obtener caché de la página actual.	public static function GetCache()	{		global $page, $site;				$cache = Site::getCache($page['id']);		$lang = $page['lang'];		if(empty($lang))			$lang = LANG;					if(!empty($site['site_translate']))			$lang = $site['site_translate'];		if(!$cache)			return false;		if(!Mem::Ready())		{			$file = BIT . 'Cache' . DS . $page['id'] . '.' . $lang . '.cache';					if(!file_exists($file))				return false;							return Io::Read($file);		}		else		{			$data = Mem::Get($page['id'] . $lang);			if($data == false)				return false;			return $data;		}	}		// Función - Establecer variable.	// - $param (String, Array): Variable.	// - $vlaue: Valor.	public static function Set($param, $value = '')	{		if(is_array($param))		{			foreach($param as $pa => $va)				self::$params[$pa] = $va;		}		else if(is_string($param))			self::$params[$param] = $value;	}		// Función - Eliminar variable.	// - $param: Variable.	public static function Del($param)	{		unset(self::$params[$param]);	}		// Función - Agregar jQuery a la página.	// - $resources: ¿Agregarlo desde los recursos locales?	public static function addjQuery($resources = true)	{		//global $site;				if($resources)		{			$file = RESOURCES_SYS . '/js/jquery.js';			//$mob = RESOURCES_SYS . '/js/jquery.mobile.js';			//$css = RESOURCES_SYS . '/css/external/jquery.mobile.css';		}		else		{			$file = '//code.jquery.com/jquery-latest.min.js';			//$mob = '//code.jquery.com/mobile/latest/jquery.mobile.min.js';			//$css = '//code.jquery.com/mobile/latest/jquery.mobile.min.css';		}				self::addScript($file);				// Si es un móvil, agregar jQuery Mobile.		/*		if(Core::isMobile() AND $site['site_mobile'] == "true")		{			self::addScript($mob);			self::addStyle($css);		}		*/	}		// Función - Agregar un elemento Meta.	// - $name: Nombre de la META.	// - $content: Contenido/Valor.	// - $type: Tipo.	public static function addMeta($name, $content, $type = 'name')	{		$html = new Html('meta');		$html->Set($type, $name);		$html->Set('content', $content);		self::$metas .= "	" . $html->Build() . "\r\n";	}		// Función - Agregar un archivo de estilo.	// - $file: Ruta del archivo CSS.	// - $rel: Rel.	// - $id: ID del elemento.	// - $media: Media.	public static function addStyle($file, $rel = 'stylesheet', $id = '', $media = '')	{		$html = new Html('link');		$html->Set('href', $file);		if(!empty($rel))			$html->Set('rel', $rel);		if(!empty($id))			$html->Set('id', $id);		if(!empty($media))			$html->Set('media', $media);				self::$styles .= "	" . $html->Build() . "\r\n";				return true;	}		// Función - Agregar un archivo de estilo con la ubicación predeterminada.	// - $file: Archivo CSS.	// - $system (Bool): ¿De los recursos globales?	// - $external (Bool) - ¿De los recursos externos?	public static function myStyle($file, $system = false, $external = false)	{		$path = !$system ? RESOURCES . '/css' : RESOURCES_SYS . '/css';		if($external)			$path = $path . '/external';					self::addStyle("$path/$file.css");	}		// Función - Agregar un archivo JavaScript.	// - $file: Ruta del archivo.	// - $async (Bool): ¿Sincronizado?	// - $id: ID del elemento.	public static function addScript($file, $async = false, $id = '')	{		$html = new Html('script');		$html->Set('src', $file);				if(!empty($id))			$html->Set('id', $id);				if($async)			$html->Set('async', $async);					self::$js .= "	" . $html->Build() . "\r\n";	}		// Función - Agregar un archivo Javascript con la ubicación predeterminada.	// - $file: Archivo JavaScript.	// - $system (Bool): ¿De los recursos globales?	// - $external (Bool) - ¿De los recursos externos?	public static function myScript($file, $system = false, $external = false)	{		$path = !$system ? RESOURCES . '/js' : RESOURCES_SYS . '/js';					if($external)			$path = $path . '/external';					self::addScript("$path/$file.js");	}		// Función - Agregar variable/función/definición JavaScript.	// - $param: Variable/Función/Definición.	// - $value: Valor.	// - $var: ¿Variable?	public static function addVar($param, $value, $var = true)	{		if($value !== 'true' AND $value !== 'false' AND $value !== 'null' AND !is_numeric($value))			$value = "\"$value\"";				if($var)			$html .= 'var ';					$html .= "$param = $value;\r\n";		self::$vars .= $html;	}		// Función - Agregar "Cosas" a la cabecera.	// - $html: HTML de tu "Cosa".	public static function addStuff($html)	{		self::$stuff .= "	$html\r\n";	}	// Función - Agregar atributo a la etiqueta <html>	// - $param: Parametro.	// - $value: Valor.	public static function addMoreHTML($param, $value = "")	{		$html = $param;		if(!empty($value))			$html .= "=\"$value\"";		self::$mhtml .= $html;	}	// Función - Agregar atributo a la etiqueta <head>	// - $param: Parametro.	// - $value: Valor.	public static function addMoreHead($param, $value = "")	{		$html = $param;		if(!empty($value))			$html .= "=\"$value\"";		self::$mhead .= $html;	}		// Función - Agregar una tarea para la barra de tareas especial para Internet Explorer 9+	// - $name: Nombre de la tarea.	// - $url: Dirección web de la tarea.	// - $icon: Dirección web del icono.	public static function IETask($name, $url, $icon = "")	{		self::addMeta('msapplication-task', "name=$name;action-uri=$url;icon-uri=$icon");	}		// Función - Ejecutar una acción JavaScript al terminar de cargar la página.	// - action: Acción JavaScript.	public static function JavaAction($action)	{		// El HTML de JavaScript esta vacio.		if(empty(self::$javascript))			self::$javascript = "<script>$(document).on(\"ready\", function() { ";					self::$javascript .= " $action ";	}		// Función - Ejecutar una alerta JavaScript.	// - $msg: Mensaje.	public static function JavaAlert($msg)	{		self::JavaAction("alert('$msg'); ");	}}?>